    public class AltaVendedorC {
        public AltaVendedorC(){
        }
        private String ultimoMensajeMySQL="";
        public String altaVendedor(String nombre, String telefono, String email) {
        String sql = "INSERT INTO picar_db.vendedores (vendedor,telefono,email) VALUES ('"+nombre+"',"+telefono+",'"+email+"');";
        try {
        Statement st = LlenarCombo.conexion.createStatement();
        st.executeUpdate(sql);
        ResultSet idvendedor = st.executeQuery("select vendedores.idvendedores from picar_db.vendedores order by vendedores.idvendedores desc limit 1;");
            if(idvendedor.next()){
        String registroid = idvendedor.getString("idvendedores");
        ultimoMensajeMySQL="El vendedor "+nombre+" fue agregado con éxito al sistema.\nSu ID es: "+registroid+".";
        JOptionPane.showMessageDialog(null, ultimoMensajeMySQL, "INFORMACIÓN DEL SISTEMA", JOptionPane.INFORMATION_MESSAGE);
        jTextField1.setText(registroid);
            }
        } catch (SQLException e) {
        System.out.println(e);
        JOptionPane.showMessageDialog(null, "ERROR: "+e, "ERROR", JOptionPane.ERROR_MESSAGE);
        }
        return ultimoMensajeMySQL;
        }
    }
    private transient AltaVendedorC avendedor = new AltaVendedorC();
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String nombre = this.jTextField2.getText();
        String telefono = this.jTextField3.getText();
        String email = this.jTextField4.getText();
        avendedor.altaVendedor(nombre.toUpperCase().trim(), telefono.replace(" ","").trim(), email.replace(" ","").toUpperCase().trim());
    }//GEN-LAST:event_jButton2ActionPerformed

