    public class BuscarVendedorC{
        public BuscarVendedorC(){
        }
        public String buscarVendedor(String idvendedor){
            try{
                Statement st = LlenarCombo.conexion.createStatement();
                ResultSet registrovendedor = st.executeQuery("SELECT * FROM picar_db.vendedores WHERE vendedores.idvendedores="+idvendedor+";");
                if(registrovendedor.next()){
                String traernombre = registrovendedor.getString("vendedor");
                String traertelefono = registrovendedor.getString("telefono");
                String traeremail = registrovendedor.getString("email"); 
                jTextField2.setText(traernombre);
                jTextField3.setText(traertelefono);
                jTextField4.setText(traeremail);
                }else{
                    JOptionPane.showMessageDialog(null, "El vendedor N°"+idvendedor+" ingresado no se encuentra en el sistema.", "ERROR", JOptionPane.ERROR_MESSAGE);
                    jTextField2.setText("");
                    jTextField3.setText("");
                    jTextField4.setText("");
                }
            } catch (SQLException e) {
            System.out.println(e);
            JOptionPane.showMessageDialog(null, "ERROR: "+e, "ERROR", JOptionPane.ERROR_MESSAGE);
            }
            return idvendedor;
        }
    }
    private transient BuscarVendedorC bvendedor = new BuscarVendedorC();
    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        String idvendedor = this.jTextField1.getText();
        bvendedor.buscarVendedor(idvendedor.replace(" ", "").trim());
        jTextField1.requestFocus();
    }//GEN-LAST:event_jButton3ActionPerformed
    public class ModificarVendedorC{
        public ModificarVendedorC(){
        }
        private String ultimoMensajeMySQL="";
        public String modificarVendedor (String idvendedor, String nombre, String telefono, String email){
        String sql = "UPDATE picar_db.vendedores SET vendedor='"+nombre+"', telefono="+telefono+", email='"+email+"' WHERE idvendedores="+idvendedor+";";
            try{
                Statement st = LlenarCombo.conexion.createStatement();
                st.executeUpdate(sql);
                ultimoMensajeMySQL="El vendedor N°"+idvendedor+" fue actualizado con los datos ingresados.";
                ResultSet registrovendedor = st.executeQuery("SELECT * FROM picar_db.vendedores WHERE idvendedores="+idvendedor+";");
                if(registrovendedor.next()){
                    JOptionPane.showMessageDialog(null, ultimoMensajeMySQL, "INFORMACIÓN DEL SISTEMA", JOptionPane.INFORMATION_MESSAGE);
                    String traerid = registrovendedor.getString("idvendedores");
                    String traernombre = registrovendedor.getString("vendedor");
                    String traertelefono = registrovendedor.getString("telefono");
                    String traeremail = registrovendedor.getString("email");
                    jTextField1.setText(traerid);
                    jTextField2.setText(traernombre);
                    jTextField3.setText(traertelefono);
                    jTextField4.setText(traeremail);
                }else{
                    JOptionPane.showMessageDialog(null, "El vendedor N°"+idvendedor+" ingresado no se encuentra en el sistema.", "ERROR", JOptionPane.ERROR_MESSAGE);
                    jTextField2.setText("");
                    jTextField3.setText("");
                    jTextField4.setText("");
                }
            } catch (SQLException e) {
            System.out.println(e);
            JOptionPane.showMessageDialog(null, "ERROR: "+e, "ERROR", JOptionPane.ERROR_MESSAGE);
        }
            return ultimoMensajeMySQL;
    }
    }
    private transient ModificarVendedorC mvendedor = new ModificarVendedorC();
    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        String idvendedor = this.jTextField1.getText();
        String nombre = this.jTextField2.getText();
        String telefono = this.jTextField3.getText();
        String email = this.jTextField4.getText();
        mvendedor.modificarVendedor(idvendedor.replace(" ", "").trim(),nombre.toUpperCase().trim(), telefono.replace(" ","").trim(), email.replace(" ","").toUpperCase().trim());
        jTextField1.requestFocus();
    }//GEN-LAST:event_jButton4ActionPerformed

